class AndroidWakeLock {
  constructor() {
    this.video = document.createElement('video')
    this.video.setAttribute('loop', '')

    function base64 (mimeType, base64) {
      return 'data:' + mimeType + ';base64,' + base64;
    }

    function addSourceToVideo (element, type, dataURI) {
      var source = document.createElement('source')
      source.src = dataURI
      source.type = 'video/' + type
      element.appendChild(source)
    }

    addSourceToVideo(this.video,'webm', base64('video/webm', 'GkXfo0AgQoaBAUL3gQFC8oEEQvOBCEKCQAR3ZWJtQoeBAkKFgQIYU4BnQI0VSalmQCgq17FAAw9CQE2AQAZ3aGFtbXlXQUAGd2hhbW15RIlACECPQAAAAAAAFlSua0AxrkAu14EBY8WBAZyBACK1nEADdW5khkAFVl9WUDglhohAA1ZQOIOBAeBABrCBCLqBCB9DtnVAIueBAKNAHIEAAIAwAQCdASoIAAgAAUAmJaQAA3AA/vz0AAA='));
    addSourceToVideo(this.video, 'mp4', base64('video/mp4', ''));
  }

  start () {
    if (this.video.paused) {
      this.video.play();
    }
  };

  stop () {
    this.video.pause();
  };
}

class iOSWakeLock {
  constructor() {
    this.timer = null;
  }

  start () {
    if (!this.timer) {
      this.timer = setInterval(function() {
        window.location = window.location;
        setTimeout(window.stop, 0);
      }, 30000);
    }
  }

  stop () {
    if (this.timer) {
      clearInterval(this.timer);
      this.timer = null;
    }
  }
}


function getWakeLock () {
  var userAgent = navigator.userAgent || navigator.vendor || window.opera;
  if (userAgent.match(/iPhone/i) || userAgent.match(/iPod/i)) {
    return iOSWakeLock;
  } else {
    return AndroidWakeLock;
  }
}


export default getWakeLock()
